import React, { useEffect, useRef, useState } from 'react';
import { Box, CircularProgress } from '@mui/material';
import { Network } from 'vis-network';
import { DataSet } from 'vis-data';
import { API_BASE_URL } from '../config';

function ProteinNetwork({ onSelectProtein }) {
  const networkRef = useRef(null);
  const networkInstanceRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const container = networkRef.current;

    const fetchData = async () => {
      try {
        setLoading(true);
        const response = await fetch(`${API_BASE_URL}/interactions`);
        if (!response.ok) throw new Error('Failed to fetch data');
        const data = await response.json();

        // 创建节点和边
        const nodes = new DataSet();
        const edges = new DataSet();
        const addedNodes = new Set();

        data.forEach(interaction => {
          const { protein1, protein2, score } = interaction;

          if (!addedNodes.has(protein1)) {
            nodes.add({ id: protein1, label: protein1 });
            addedNodes.add(protein1);
          }
          if (!addedNodes.has(protein2)) {
            nodes.add({ id: protein2, label: protein2 });
            addedNodes.add(protein2);
          }

          edges.add({
            from: protein1,
            to: protein2,
            width: parseFloat(score) * 2,
            color: parseFloat(score) === 1 ? '#ff0000' : '#999999'
          });
        });

        // 配置网络选项
        const options = {
          nodes: {
            shape: 'dot',
            size: 16,
            font: {
              size: 12,
              color: '#333333'
            },
            borderWidth: 2,
            color: {
              background: '#ffffff',
              border: '#2B7CE9'
            }
          },
          edges: {
            smooth: {
              type: 'continuous'
            }
          },
          physics: {
            stabilization: true,
            barnesHut: {
              gravitationalConstant: -80000,
              springConstant: 0.001,
              springLength: 200
            }
          },
          interaction: {
            hover: true,
            tooltipDelay: 200
          }
        };

        // 销毁旧的网络实例
        if (networkInstanceRef.current) {
          networkInstanceRef.current.destroy();
        }

        // 创建新的网络实例
        networkInstanceRef.current = new Network(container, { nodes, edges }, options);

        // 添加事件监听器
        networkInstanceRef.current.on('selectNode', (params) => {
          if (params.nodes.length > 0) {
            onSelectProtein(params.nodes[0]);
          }
        });

      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    return () => {
      if (networkInstanceRef.current) {
        networkInstanceRef.current.destroy();
        networkInstanceRef.current = null;
      }
    };
  }, [onSelectProtein]);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="500px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="500px" color="error.main">
        Error: {error}
      </Box>
    );
  }

  return (
    <Box
      ref={networkRef}
      sx={{
        height: '500px',
        border: '1px solid #ddd',
        borderRadius: 1,
        overflow: 'hidden'
      }}
    />
  );
}

export default ProteinNetwork;
