import React, { useState } from 'react';
import { Box, Container, Grid, TextField, Button, Typography } from '@mui/material';
import ProteinNetwork from './ProteinNetwork';
import ProteinDetails from './ProteinDetails';

function App() {
  const [selectedProtein, setSelectedProtein] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  return (
    <Container maxWidth="xl">
      <Box sx={{ my: 4 }}>
        <Grid container spacing={3}>
          <Grid item xs={8}>
            <TextField
              fullWidth
              label="搜索蛋白质"
              variant="outlined"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  setSelectedProtein(searchTerm);
                }
              }}
              sx={{ mb: 2 }}
            />
            <Button 
              variant="contained" 
              onClick={() => setSelectedProtein(searchTerm)}
              sx={{ mb: 2 }}
            >
              搜索
            </Button>
            <Typography variant="body2" sx={{ mb: 2 }}>
              示例:
              {[
                'NDH07G27970.1',
                'NDH05G05590.1', 
                'NDH10G04270.1',
                'NDH18G16570.1',
                'NDH10G01520.1',
                'NDH09G31440.1'
              ].map((id) => (
                <span 
                  key={id}
                  onClick={() => {
                    setSearchTerm(id);
                    setSelectedProtein(id);
                  }}
                  style={{ 
                    cursor: 'pointer',
                    margin: '0 4px',
                    textDecoration: 'underline',
                    color: '#1976d2'
                  }}
                >
                  {id}
                </span>
              ))}
            </Typography>
            <ProteinNetwork 
              onSelectProtein={setSelectedProtein}
            />
          </Grid>
          <Grid item xs={4}>
            <ProteinDetails protein={selectedProtein} />
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
}

export default App;
