import React from 'react';
import { Box, Typography, Paper, Divider } from '@mui/material';

function ProteinDetails({ protein }) {
  if (!protein) {
    return (
      <Paper elevation={3} sx={{ p: 2, height: '100%' }}>
        <Typography variant="h6" color="textSecondary">
          请选择一个蛋白质查看详细信息
        </Typography>
      </Paper>
    );
  }

  return (
    <Paper elevation={3} sx={{ p: 2, height: '100%' }}>
      <Typography variant="h5" gutterBottom>
        {protein.id}
      </Typography>
      <Divider sx={{ my: 2 }} />

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">序列信息</Typography>
        <Typography variant="body2" sx={{ wordBreak: 'break-word' }}>
          {protein.sequence}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">生物过程</Typography>
        <Typography variant="body2">
          {protein.Biological_process || '无信息'}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">细胞定位</Typography>
        <Typography variant="body2">
          {protein.Cellular_component || '无信息'}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">分子功能</Typography>
        <Typography variant="body2">
          {protein.Molecular_function || '无信息'}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">KOG分类</Typography>
        <Typography variant="body2">
          {protein.KOG || '无信息'}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">KEGG通路</Typography>
        <Typography variant="body2">
          {protein.KEGG_Pathways || '无信息'}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">IPRs</Typography>
        <Typography variant="body2">
          {protein.IPRs || '无信息'}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">亚细胞定位</Typography>
        <Typography variant="body2">
          {protein.Subcellular_localization || '无信息'}
        </Typography>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Typography variant="subtitle1">信号肽</Typography>
        <Typography variant="body2">
          {protein.signalP || '无信息'}
        </Typography>
      </Box>

      {/* 标签容器 */}
      <Box sx={{ 
        display: 'flex',
        flexWrap: 'wrap',
        gap: 1,
        mt: 2
      }}>
        {protein.KEGG_Pathways && (
          <Box sx={{
            bgcolor: '#e3f2fd',
            borderRadius: 1,
            px: 1,
            py: 0.5
          }}>
            <Typography variant="caption">
              KEGG通路: {protein.KEGG_Pathways}
            </Typography>
          </Box>
        )}
        {protein.Subcellular_localization && (
          <Box sx={{
            bgcolor: '#f3e5f5',
            borderRadius: 1,
            px: 1,
            py: 0.5
          }}>
            <Typography variant="caption">
              亚细胞定位: {protein.Subcellular_localization}
            </Typography>
          </Box>
        )}
        {protein.signalP && (
          <Box sx={{
            bgcolor: '#fff3e0',
            borderRadius: 1,
            px: 1,
            py: 0.5
          }}>
            <Typography variant="caption">
              信号肽: {protein.signalP}
            </Typography>
          </Box>
        )}
      </Box>
    </Paper>
  );
}

export default ProteinDetails;
